import React, { Component } from 'react';
import axios from 'axios';
import {Progress} from 'reactstrap';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import Cookies from 'universal-cookie';
import './App.css';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Bootstrap from "react-bootstrap";
import {FormGroup, FormControl} from "react-bootstrap";
import { Link } from 'react-router-dom';

class WorkingArea extends Component {

constructor(props) {
  super(props);
}

componentDidMount(){
// OnLoad function
this.heading.innerHTML = this.props.location.state.userName+"</br>Start Your Data Science Jounrney with XP-2";
}


testdownloadapi = () =>{
  console.log("inside the testnodeapi function : ")
  axios.post("http://localhost:4000/download/",{
    username : 'sathyaswarupa',
    videoname : this.videoname,
    videourl : 'https://www.youtube.com/watch?v=1Ap2G1Bfm0M'
  })
    .then(res => { // then print response status
      //toast.success('upload success')
      console.log(res)
    })
    .catch(err => {
    // then print response status
    //  toast.error('upload fail')
    console.log("fail")
    console.log(err)
    })
}


handleSubmit = () =>{
  var data = this.usercredentials.value+","+this.Password.value
  axios.post("http://192.168.1.8:8080/authorizeuser",data)
    .then(res => { // then print response status
      if(res.data["message"] == "No"){
        this.Error.innerHTML = "UserName or Password incorrect."
      }else{
        const cookies = new Cookies()
        cookies.set('username',this.usercredentials.value, { path: '/' })
        this.props.history.push({
          pathname: '/customrouting',
          state: {usercredentials: this.usercredentials.value, checkval : res.data["message"]}
        })
      }
    })
    .catch(err => { // then print response status
    //  toast.error('upload fail')
    console.log(err)
    })
}

Logout = () =>{
    const cookies = new Cookies()
    cookies.remove('username');
    window.location.reload(false);
}

render() {
    return (
      <div className = "BackgroundSign">
      <h1 className = "AppName" ref = {c => this.heading = c}></h1>
      <div className="SignIn">
        <form onSubmit={this.handleSubmit}>
        <p class = "SignInHead">X-P2</p>
        <p class = "SignUpHead">Begin the data gathering process with just a few steps.</p>
        &nbsp;
          <FormGroup controlId="url" bsSize="large">
            <FormControl
              autoFocus
              placeholder="Enter the url of the youtube video"
              ref = {c => this.videourl = c}
            />
          </FormGroup>

          <FormGroup controlId="url" bsSize="large">
            <FormControl
              autoFocus
              placeholder="Enter the url of the youtube video"
              ref = {c => this.videoname = c}
            />
          </FormGroup>

          <Button className="StartButton" block bsSize="large" onClick={this.handleSubmit} type="button">
            Start
          </Button>
          <br/>
          <p className = "ErrorMessage" ref = {c => this.Error = c}></p>
        </form>
      </div>
      <div className="SecondBoxSignIn">
        <p className = "LinkToAccount"> Done downloading the files?&nbsp;
          <Link className="LinkToSignUp" onClick={this.Logout}>Log out</Link>
        </p>
      </div>
      </div>
    );
  }
}

export default WorkingArea;
